import BaseDialog, { IDialogConfiguration } from './BaseDialog';
/**
 * Internal implementation of a dialog to mimic browser's prompt() behavior.
 * Third-party can use this by calling the static method Dialog.prompt()
 *
 * @internal
 */
export default class PromptDialog extends BaseDialog {
    private _message;
    private _defaultValue;
    private _resolve;
    constructor(message: string, resolve: (value: string) => void, defaultValue?: string);
    readonly config: IDialogConfiguration;
    resolve(value: string): void;
    render(): void;
}
